import { USER_REQUEST, USER_SUCCESS, USER_FAILURE } from './userTypes';

export const fetchUsersRequest = () => {
    return{
        type: USER_REQUEST,
    }
}

export const fetchUsersSuccess = (users) => {
    return{
        type: USER_SUCCESS,
        payload: users,
    }
}

export const fetchUsersFailure = (error) => {
    return{
        type: USER_FAILURE,
        payload: error,
    }
}
