import { USER_REQUEST, USER_SUCCESS, USER_FAILURE } from './userTypes';

export const fetchUsersRequest = () => {
    return{
        type: USER_REQUEST,
    }
}

const fetchUsersSuccess = (users) => {
    return{
        type: USER_REQUEST,
        payload: users,
    }
}

const fetchUsersFailure = (error) => {
    return{
        type: USER_REQUEST,
        payload: error,
    }
}
